import React, { useEffect, useState, Suspense } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css'
import ThreeDScene from "./components/AboutBG"; // 3D scene component
// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

const App = () => {
  const [isTitleBGVisible, setTitleBGVisible] = useState(true);
  const [index, setIndex] = useState(0); // Track the index of the active element

  const handleArrowClick = (direction) => {
    const totalItems = 3; // There are 3 items: About Me, Skills, Experience
    setIndex((prevIndex) => {
      if (direction === 'left') {
        return prevIndex === 0 ? totalItems - 1 : prevIndex - 1;
      } else {
        return prevIndex === totalItems - 1 ? 0 : prevIndex + 1;
      }
    });
  };

  useEffect(() => {
    // Trigger horizontal slide animation when index changes
    const items = document.querySelectorAll('.about-item');
    const currentItem = items[index];

    // Animate the current item to slide in/out
    gsap.to(currentItem, {
      opacity: 1,
      x: 0,
      duration: 0.5,
      ease: 'power2.out',
    });

    // Animate the previous item to slide out
    const prevIndex = index === 0 ? 2 : index - 1; // Get the previous item index
    const prevItem = items[prevIndex];
    gsap.to(prevItem, {
      opacity: 0,
      x: -200,
      duration: 0.5,
      ease: 'power2.in',
    });
  }, [index]); // Re-run the effect when the index changes

  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50} />
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      <section className="about" id="about">
        {/* Left Arrow */}
        <div className="arrow left" onClick={() => handleArrowClick('left')}>&#8592;</div>

        {/* About Me, Skills, Experience Elements */}
        <div className="about-content">
          <div className="about-item" id="about-me">About Me</div>
          <div className="about-item" id="skills">Skills</div>
          <div className="about-item" id="experience">Experience</div>
        </div>

        {/* Right Arrow */}
        <div className="arrow right" onClick={() => handleArrowClick('right')}>&#8594;</div>

        {/* Rectangle Element */}
        <div className="rectangle"></div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

// Throttle function for IntersectionObserver callback
const throttle = (func, wait) => {
  let timeout = null;
  return function () {
    const context = this;
    const args = arguments;
    if (!timeout) {
      timeout = setTimeout(() => {
        timeout = null;
        func.apply(context, args);
      }, wait);
    }
  };
};

export default App;
