import React, { useState, useEffect, useRef } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import { useSpring, animated } from '@react-spring/web';

// Lazy load TitleBG component and ensure it only loads once
const TitleBG = React.lazy(() => import('./components/TitleBG'));
import Preloader from './components/Preloader'; 

const AnimatedText = React.memo(({ text, onAnimationComplete }) => {
  const splitText = text.split("");

  const springs = splitText.map((letter, index) =>
    useSpring({
      opacity: 1,
      transform: `translateY(0)`,
      from: { opacity: 0, transform: `translateY(20px)` },
      delay: index * 100,
      config: { duration: 500 },
      onRest: index === splitText.length - 1 ? onAnimationComplete : undefined,
    })
  );

  return (
    <animated.h1>
      {splitText.map((letter, index) => (
        <animated.span key={index} style={springs[index]}>
          {letter}
        </animated.span>
      ))}
    </animated.h1>
  );
});

const App = () => {
  const [animationCompleted, setAnimationCompleted] = useState(false);
  const [preloaderVisible, setPreloaderVisible] = useState(true);
  const [bgLoaded, setBgLoaded] = useState(false);
  const [sceneLoaded, setSceneLoaded] = useState(false);
  const titleBgRef = useRef(null);

  useEffect(() => {
    if (sceneLoaded) return; // Prevent reloading if already loaded

    // Loading light effects only once
    const sections = document.querySelectorAll('section');
    sections.forEach((section, sectionIndex) => {
      const lightConfig = getLightConfigForSection(sectionIndex);
      lightConfig.forEach(({ color, count, positions }) => {
        for (let i = 0; i < count; i++) {
          const light = document.createElement('div');
          light.classList.add('light-effect');
          light.style.background = `radial-gradient(circle, ${color} 25%, ${color} 90%)`;
          light.style.boxShadow = `0 0 250px 150px ${color}`;
          const { positionX, positionY } = positions[i];
          light.style.left = `${positionX}%`;
          light.style.top = `${positionY}%`;
          section.appendChild(light);
          animateLightEffect(light);
        }
      });
    });

    setSceneLoaded(true); // Mark the scene as loaded
  }, [sceneLoaded]);

  useEffect(() => {
    if (animationCompleted && titleBgRef.current) {
      gsap.fromTo(titleBgRef.current, 
        { opacity: 0, scale: 0.8 },  
        { opacity: 1, scale: 1, duration: 1, ease: 'power2.out' }
      );
    }
  }, [animationCompleted]);

  useEffect(() => {
    if (bgLoaded) {
      setPreloaderVisible(false); // Hide preloader after background is loaded
    }
  }, [bgLoaded]);

  const getLightConfigForSection = (sectionIndex) => {
    switch (sectionIndex) {
      case 0:
        return [{ color: 'rgba(0, 255, 255, 0.3)', count: 1, positions: [{ positionX: 110, positionY: 50 }] }];
      case 1:
        return [{ color: 'rgba(255, 0, 255, 0.3)', count: 1, positions: [{ positionX: -15, positionY: 50 }] }];
      case 2:
        return [{ color: 'rgba(0, 255, 0, 0.3)', count: 1, positions: [{ positionX: 100, positionY: 50 }] }];
      case 3:
        return [{ color: 'rgba(255, 255, 0, 0.3)', count: 1, positions: [{ positionX: 20, positionY: 30 }] }];
      default:
        return [];
    }
  };

  const animateLightEffect = (lightElement) => {
    gsap.to(lightElement, {
      scale: 'random(1, 1.5)',
      opacity: 'random(0.4, 1)',
      x: 'random(-80, 80)',
      y: 'random(-80, 80)',
      duration: 4,
      repeat: -1,
      yoyo: true,
      ease: 'power1.inOut',
    });
  };

  const handleAnimationComplete = () => {
    setAnimationCompleted(true);
  };

  const handleBgLoaded = () => {
    setBgLoaded(true);
  };

  return (
    <div className="container">
      {preloaderVisible && <Preloader />}

      <section className="hero" id="hero">
        <React.Suspense fallback={<div>Loading background...</div>}>
          {/* Only load TitleBG once */}
          {!bgLoaded && <TitleBG onLoaded={handleBgLoaded} />}
        </React.Suspense>

        <div className="hero-content">
          <AnimatedText text="Welcome to My Portfolio" onAnimationComplete={handleAnimationComplete} />
          <p>3D Artist | Animator | Technical Support Engineer</p>
          <a href="#about" className="btn explore">Explore My Work</a>
        </div>
      </section>

      <section className="about" id="about">
        <div className="glass-effect">
          <h2>About Me</h2>
          <p>Some description about your work, skills, and experience.</p>
          <a href="#contact" className="btn contact">Contact Me</a>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

export default App;
