import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';
import { gsap } from 'gsap';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const bigSphereRef = useRef(null);
  const smallSphereRef = useRef(null);
  const [mouseX, setMouseX] = useState(0);

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.outputEncoding = THREE.sRGBEncoding;
    renderer.physicallyCorrectLights = true;
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    cameraRef.current = camera;
    sceneRef.current = scene;

    camera.position.z = 5;

    // Load HDR for lighting
    const rgbeLoader = new RGBELoader();
    rgbeLoader.load('/assets/brown_photostudio_02_2k.hdr', (texture) => {
      texture.mapping = THREE.EquirectangularReflectionMapping;
      scene.environment = texture;
      scene.background = new THREE.Color(0x000000);
    });

    // Big Sphere (Stationary)
    const bigSphereGeometry = new THREE.SphereGeometry(1, 32, 32);
    const bigSphereMaterial = new THREE.MeshPhysicalMaterial({
      color: 0xffffff,
      metalness: 0.1,
      roughness: 0.6,
      clearcoat: 0.3,
      clearcoatRoughness: 0.3,
    });
    const bigSphere = new THREE.Mesh(bigSphereGeometry, bigSphereMaterial);
    scene.add(bigSphere);
    bigSphereRef.current = bigSphere;

    // Small Sphere (Will rotate around the big sphere)
    const smallSphereGeometry = new THREE.SphereGeometry(0.3, 32, 32);
    const smallSphereMaterial = new THREE.MeshPhysicalMaterial({
      color: 0xff0000,
      metalness: 0.2,
      roughness: 0.5,
      clearcoat: 0.35,
      clearcoatRoughness: 0.3,
    });
    const smallSphere = new THREE.Mesh(smallSphereGeometry, smallSphereMaterial);
    smallSphere.position.set(2, 0, 0); // Initial position behind the big sphere
    scene.add(smallSphere);
    smallSphereRef.current = smallSphere;

    // Update mouse position to control rotation
    const handleMouseMove = (event) => {
      const mouseXPosition = (event.clientX / window.innerWidth) * 2 - 1; // Normalized X-axis (-1 to 1)
      setMouseX(mouseXPosition);
    };

    window.addEventListener('mousemove', handleMouseMove);

    // GSAP animation for small sphere movement
    const animateSmallSphere = () => {
      const angle = mouseX * Math.PI; // Map mouseX to an angle (-π to π)
      const targetX = 2 * Math.cos(angle); // Orbit distance 2 (Adjust if necessary)
      const targetZ = 2 * Math.sin(angle); // Orbit distance 2 (Adjust if necessary)

      // Smooth animation using GSAP
      gsap.to(smallSphere.position, {
        x: targetX,
        z: targetZ,
        duration: 0.5, // Adjust duration for smoothness
        ease: 'power3.out', // Smooth easing function
      });
    };

    // Animation Loop
    const animate = () => {
      if (bigSphereRef.current && smallSphereRef.current) {
        // The big sphere stays stationary
        bigSphereRef.current.rotation.y += 0.01; // Rotate big sphere for added visual effect (optional)

        animateSmallSphere(); // Animate small sphere
      }
      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    };
    animate();

    // Resize Event Listener
    const onWindowResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize);

    return () => {
      window.removeEventListener('resize', onWindowResize);
      window.removeEventListener('mousemove', handleMouseMove);
      renderer.dispose();
      bigSphereGeometry.dispose();
      bigSphereMaterial.dispose();
      smallSphereGeometry.dispose();
      smallSphereMaterial.dispose();
      scene.clear();
    };
  }, [mouseX]);

  return null;
};

export default TitleBG;
