import React, { useEffect, useState, Suspense } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css'
import ThreeDScene from "./components/AboutBG"; // 3D scene component

// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

const App = () => {
  const [isTitleBGVisible, setTitleBGVisible] = useState(true); // Set to true to load TitleBG immediately

  function App() {
    // State to track the active section
    const [activeSection, setActiveSection] = useState("about-me");

    // Function to handle section change
    const handleSectionChange = (section) => {
      setActiveSection(section); // Update the active section
    };
  }
  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          {/* Apply SplitText to the Hero text */}
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50}/>
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      return (
    <section className="about" id="about">
      {/* Horizontal Scroll Menu */}
      <div className="horizontal-menu">
        <div className="menu-wrapper">
          <button
            className={`menu-item ${activeSection === "about-me" ? "active" : ""}`}
            onClick={() => handleSectionChange("about-me")}
          >
            About Me
          </button>
          <button
            className={`menu-item ${activeSection === "skills" ? "active" : ""}`}
            onClick={() => handleSectionChange("skills")}
          >
            Skills
          </button>
          <button
            className={`menu-item ${activeSection === "experience" ? "active" : ""}`}
            onClick={() => handleSectionChange("experience")}
          >
            Experience
          </button>
        </div>
      </div>

      {/* Dynamic Content Area */}
      <div className="content-section">
        <div className="dynamic-content">
          {activeSection === "about-me" && (
            <div className="content">
              <h2>About Me</h2>
              <p>I am a passionate 3D artist and animator...</p>
            </div>
          )}
          {activeSection === "skills" && (
            <div className="content">
              <h2>Skills</h2>
              <div className="skills-container">
                <div className="skill">
                  <img src="path/to/software-logo1.png" alt="Software" />
                  <div className="progress-bar">
                    <div className="progress" style={{ width: "80%" }}></div>
                  </div>
                </div>
                <div className="skill">
                  <img src="path/to/software-logo2.png" alt="Software" />
                  <div className="progress-bar">
                    <div className="progress" style={{ width: "60%" }}></div>
                  </div>
                </div>
              </div>
            </div>
          )}
          {activeSection === "experience" && (
            <div className="content">
              <h2>Experience</h2>
              <p>Details about your experience...</p>
            </div>
          )}
        </div>
        <div className="corner-square"></div>
        <div className="coin-container">
          <ThreeDScene />
        </div>
      </div>
    </section>
  );



      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

// Throttle function for IntersectionObserver callback
const throttle = (func, wait) => {
  let timeout = null;
  return function () {
    const context = this;
    const args = arguments;
    if (!timeout) {
      timeout = setTimeout(() => {
        timeout = null;
        func.apply(context, args);
      }, wait);
    }
  };
};

export default App;
