import React, { useState, useEffect, Suspense } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css';
import ThreeDScene from "./components/AboutBG"; // 3D scene component
// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

// Import react-scroll functions
import { Link, Element, scroller } from 'react-scroll';

const App = () => {
  const [index, setIndex] = useState(0);  // Start at the first element ("About Me")

  // Handle scroll on clicking the arrows
  const handleArrowClick = (direction) => {
    const totalItems = 3;
    setIndex((prevIndex) => {
      if (direction === 'left') {
        return prevIndex === 0 ? totalItems - 1 : prevIndex - 1;
      } else {
        return prevIndex === totalItems - 1 ? 0 : prevIndex + 1;
      }
    });
  };

  useEffect(() => {
    // Smoothly scroll to the current section
    scroller.scrollTo(`section-${index}`, {
      duration: 800,
      smooth: 'easeInOutQuart',
      offset: -100,  // Slight offset for better visibility
    });

    // Animate the section change with GSAP
    gsap.fromTo(
      `.about-item:nth-child(${index + 1})`,
      { opacity: 0, y: 50 },
      { opacity: 1, y: 0, duration: 1, ease: "power2.out" }
    );

  }, [index]);

  useEffect(() => {
    // Initial animation for the first section
    gsap.from('.hero-content', { opacity: 0, y: -100, duration: 1, delay: 0.5 });
    gsap.from('.btn.explore', { opacity: 0, x: -100, duration: 1, delay: 1 });
  }, []);

  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50} />
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      <section className="about" id="about">
        {/* Left Arrow */}
        <div className="arrow left" onClick={() => handleArrowClick('left')}>&#8592;</div>

        {/* About Me, Skills, Experience Elements */}
        <div className="about-content">
          <div className="about-item">
            <Link to="section-0" smooth={true} duration={800}>
              About Me
            </Link>
          </div>
          <div className="about-item">
            <Link to="section-1" smooth={true} duration={800}>
              Skills
            </Link>
          </div>
          <div className="about-item">
            <Link to="section-2" smooth={true} duration={800}>
              Experience
            </Link>
          </div>
        </div>

        {/* Right Arrow */}
        <div className="arrow right" onClick={() => handleArrowClick('right')}>&#8594;</div>

        {/* Rectangle Element */}
        <div className="rectangle"></div>
      </section>

      {/* Section elements with specific ids */}
      <Element name="section-0">
        <section className="portfolio" id="portfolio">
          <div className="glass-effect">
            <h2>My Portfolio</h2>
            <p>Showcase of projects like 3D models, animations, etc.</p>
          </div>
        </section>
      </Element>

      <Element name="section-1">
        <section className="contact" id="contact">
          <div className="glass-effect">
            <h2>Skills</h2>
            <p>Details of my technical and soft skills.</p>
          </div>
        </section>
      </Element>

      <Element name="section-2">
        <section className="contact" id="contact">
          <div className="glass-effect">
            <h2>Experience</h2>
            <p>My experience as a 3D artist and animator.</p>
          </div>
        </section>
      </Element>
    </div>
  );
};

export default App;
