import React, { useRef, useEffect, useState } from 'react';
import * as THREE from 'three';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';
import { gsap } from 'gsap';
import { throttle } from 'lodash';
import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass';
import { FilmPass } from 'three/examples/jsm/postprocessing/FilmPass';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const bigSphereRef = useRef(null);
  const smallSphereRef = useRef(null);
  const composerRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const scene = new THREE.Scene();
    const aspect = window.innerWidth / window.innerHeight;
    const camera = new THREE.OrthographicCamera(
      -2 * aspect, 2 * aspect, 2, -2, 0.1, 1000
    );
    const renderer = new THREE.WebGLRenderer({ antialias: true, preserveDrawingBuffer: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.outputEncoding = THREE.sRGBEncoding;
    renderer.physicallyCorrectLights = true;
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    cameraRef.current = camera;
    sceneRef.current = scene;
    camera.position.z = 5;

    // Set up the loading manager
    const loadingManager = new THREE.LoadingManager();
    loadingManager.onStart = () => console.log("Loading started...");
    loadingManager.onLoad = () => {
      console.log("Loading complete!");
      setIsLoading(false); // Set loading to false once all assets are loaded

      // Trigger popup animation after loading (models come from the center)
      gsap.fromTo(bigSphereRef.current.scale, { x: 0, y: 0, z: 0 }, { x: 1, y: 1, z: 1, duration: 1, ease: 'power3.out' });
      gsap.fromTo(smallSphereRef.current.scale, { x: 0, y: 0, z: 0 }, { x: 1, y: 1, z: 1, duration: 1, ease: 'power3.out' });

      // Optional position animation (to create slight "pop-in" effect)
      gsap.fromTo(bigSphereRef.current.position, { y: 0 }, { y: 0, duration: 1, ease: 'power3.out' });
      gsap.fromTo(smallSphereRef.current.position, { y: 0 }, { y: 0, duration: 1, ease: 'power3.out' });
    };
    loadingManager.onProgress = (url, itemsLoaded, itemsTotal) => {
      console.log(`Loading: ${url}`);
    };
    loadingManager.onError = (url) => {
      console.log(`Error loading: ${url}`);
    };

    // Define materials for both spheres with baked lighting texture
    const bakedTexture = new THREE.TextureLoader().load('/assets/baked_light_texture.jpg'); // Replace with your baked texture path
    const bakedMaterial = new THREE.MeshStandardMaterial({
      map: bakedTexture, // Using the precomputed baked light texture
      metalness: 0.5,
      roughness: 0.5
    });

    const bigSphereGeometry = new THREE.SphereGeometry(1, 32, 32);
    const smallSphereGeometry = new THREE.SphereGeometry(0.3, 32, 32);
    const bigSphere = new THREE.Mesh(bigSphereGeometry, bakedMaterial);
    const smallSphere = new THREE.Mesh(smallSphereGeometry, bakedMaterial);

    // Group the spheres to make them easier to manage
    const sphereGroup = new THREE.Group();
    sphereGroup.add(bigSphere);
    sphereGroup.add(smallSphere);
    scene.add(sphereGroup);

    bigSphereRef.current = bigSphere;
    smallSphereRef.current = smallSphere;

    // Basic lights setup for initial scene rendering
    const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.1);
    directionalLight1.position.set(3, 3, 3);
    scene.add(directionalLight1);

    const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
    directionalLight2.position.set(-3, -3, 3);
    scene.add(directionalLight2);

    // Throttle mouse events to reduce load
    const handleMouseMove = throttle((event) => {
      const mouseXPosition = (event.clientX / window.innerWidth) * 2 - 1;
      const mouseYPosition = (event.clientY / window.innerHeight) * 2 - 1;
      const angleX = mouseXPosition * Math.PI;
      const angleY = mouseYPosition * Math.PI;
      const radius = 1;

      const targetX = radius * Math.cos(angleX);
      const targetY = radius * Math.sin(angleY);
      const targetZ = -1;

      gsap.to(smallSphere.position, { x: targetX, y: targetY, z: targetZ, duration: 0.5, ease: 'power3.out' });
    }, 20); // Throttle to every 20ms

    window.addEventListener('mousemove', handleMouseMove);

    // Post-processing setup (keeps it simple for now)
    const composer = new EffectComposer(renderer);
    composer.addPass(new RenderPass(scene, camera));
    composer.addPass(new FilmPass(0.2, 0.025, 648, false)); // Lower effect strength
    composerRef.current = composer;

    // Load environment map and textures asynchronously (delayed)
    const rgbeLoader = new RGBELoader(loadingManager);
    rgbeLoader.load('/assets/brown_photostudio_02_2k.hdr', (texture) => {
      texture.mapping = THREE.EquirectangularReflectionMapping;
      scene.environment = texture;
      scene.background = new THREE.Color(0x000000);
    });

    // Preload all assets like textures and baked maps before rendering
    const textureLoader = new THREE.TextureLoader(loadingManager);
    const bigSphereTexture = textureLoader.load('assets/Poliigon_MetalPaintedMatte_7037_BaseColor.jpg'); // Example texture path

    // Use the preloaded texture for big sphere material
    bakedMaterial.map = bigSphereTexture;
    bakedMaterial.needsUpdate = true;

    // Animation loop for smooth rendering
    const animate = () => {
      if (!isLoading) {
        composer.render();
      }
      requestAnimationFrame(animate);
    };
    animate();

    // Resize handling for responsiveness
    const onWindowResize = () => {
      const aspect = window.innerWidth / window.innerHeight;
      camera.left = -2 * aspect;
      camera.right = 2 * aspect;
      camera.top = 2;
      camera.bottom = -2;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize);

    // Cleanup when the component is unmounted
    return () => {
      window.removeEventListener('resize', onWindowResize);
      window.removeEventListener('mousemove', handleMouseMove);
      renderer.dispose();
      bigSphereGeometry.dispose();
      bakedMaterial.dispose();
      smallSphereGeometry.dispose();
      bakedMaterial.dispose();
      scene.clear();
    };
  }, [isLoading]);

  return (
    <div>
      {isLoading && (
        <div className="loading-screen">
          <h3>Loading...</h3>
        </div>
      )}
    </div>
  );
};

export default TitleBG;
