import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer.js';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass.js';
import { UnrealBloomPass } from 'three/examples/jsm/postprocessing/UnrealBloomPass.js';
import { ToneMappingPass } from 'three/examples/jsm/postprocessing/ToneMappingPass.js';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const composerRef = useRef(null);

  useEffect(() => {
    // Set up scene, camera, and renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    camera.position.z = 5;
    cameraRef.current = camera;
    
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;

    // Set up post-processing
    const composer = new EffectComposer(renderer);
    composer.addPass(new RenderPass(scene, camera));

    // Add Bloom effect for realistic lighting
    const bloomPass = new UnrealBloomPass(
      new THREE.Vector2(window.innerWidth, window.innerHeight),
      1.5, // Strength of bloom effect
      0.4, // Bloom radius
      0.85 // Bloom threshold
    );
    composer.addPass(bloomPass);

    // Tone mapping for better lighting and shading
    const toneMappingPass = new ToneMappingPass();
    composer.addPass(toneMappingPass);

    composerRef.current = composer;

    // Set up environment lighting and HDR (if you have a cube map for realistic HDR lighting)
    const hdrTexture = new THREE.CubeTextureLoader()
      .setPath('/path/to/your/hdr/texture/')
      .load([
        'px.jpg', 'nx.jpg', 'py.jpg', 'ny.jpg', 'pz.jpg', 'nz.jpg',
      ]);

    scene.background = hdrTexture;
    scene.environment = hdrTexture;

    // Lighting Setup (Ambient + Directional + Point Lights)
    const ambientLight = new THREE.AmbientLight(0x404040, 0.5); // Soft white light
    scene.add(ambientLight);

    const directionalLight = new THREE.DirectionalLight(0xffffff, 1);
    directionalLight.position.set(5, 5, 5);
    directionalLight.castShadow = true;
    scene.add(directionalLight);

    const pointLight = new THREE.PointLight(0xffa500, 1, 100); // Orange point light for shine effect
    pointLight.position.set(2, 2, 2);  // Position the point light slightly above and to the side
    scene.add(pointLight);

    // Create materials with smooth shiny texture
    const smoothMaterial = new THREE.MeshPhysicalMaterial({
      color: 0xffa500,  // Orange color
      metalness: 1,     // Full metalness for smooth, shiny appearance
      roughness: 0.05,  // Very low roughness to make it smooth and reflective
      emissive: 0xffa500, // Glowing effect
      clearcoat: 1,      // Extra layer of smoothness
      clearcoatRoughness: 0, // Smooth clearcoat
    });

    // Create two spheres
    const largeSphereGeometry = new THREE.SphereGeometry(1);
    const smallSphereGeometry = new THREE.SphereGeometry(0.5);

    const largeSphere = new THREE.Mesh(largeSphereGeometry, smoothMaterial);
    const smallSphere = new THREE.Mesh(smallSphereGeometry, smoothMaterial);

    // Position the spheres (small sphere behind large sphere)
    largeSphere.position.set(0, 0, 0);
    smallSphere.position.set(0, 0, -2); // Behind the large sphere

    // Add spheres to the scene
    scene.add(largeSphere);
    scene.add(smallSphere);

    // Animation loop
    const animate = () => {
      largeSphere.rotation.x += 0.01;
      largeSphere.rotation.y += 0.01;
      smallSphere.rotation.x += 0.01;
      smallSphere.rotation.y += 0.01;

      // Use post-processing for final render
      composer.render();
      requestAnimationFrame(animate);
    };
    animate();

    // Cleanup when component unmounts
    return () => {
      renderer.dispose();
      composer.dispose();
      scene.remove(largeSphere);
      scene.remove(smallSphere);
    };
  }, []);

  return <></>;
};

export default TitleBG;
