import { EffectComposer, RenderPass, ShaderPass } from 'three/examples/jsm/postprocessing/EffectComposer.js';
import { UnrealBloomPass } from 'three/examples/jsm/postprocessing/UnrealBloomPass.js';
import { ToneMappingPass } from 'three/examples/jsm/postprocessing/ToneMappingPass.js';
import { ShaderMaterial } from 'three';

export const createPostProcessing = (scene, camera, renderer) => {
  const composer = new EffectComposer(renderer);
  const renderPass = new RenderPass(scene, camera);
  composer.addPass(renderPass);

  // Add UnrealBloomPass for a glowing effect (you can adjust the strength and radius)
  const bloomPass = new UnrealBloomPass();
  composer.addPass(bloomPass);

  // Add ToneMappingPass for realistic lighting effects
  const toneMappingPass = new ToneMappingPass();
  composer.addPass(toneMappingPass);

  return composer;
};
