import React, { useState } from 'react';
import './styles/App.css';
import gsap from 'gsap';

const App = () => {
  const [index, setIndex] = useState(0);  // Tracks current section index

  // Handle arrow click to change section
  const handleArrowClick = (direction) => {
    const totalItems = 3;  // Number of items to cycle through ("About Me", "Skills", "Experience")
    let newIndex = index;

    // Calculate new index based on direction (left or right)
    if (direction === 'left') {
      newIndex = index === 0 ? totalItems - 1 : index - 1;  // Move to the previous item or loop back
    } else {
      newIndex = index === totalItems - 1 ? 0 : index + 1;  // Move to the next item or loop back
    }

    // Trigger the slide-out and slide-in animations using GSAP
    gsap.to('.about-content', {
      x: direction === 'left' ? '+=' + window.innerWidth : '-=' + window.innerWidth, 
      duration: 1,
      onComplete: () => {
        // Set the new index and reset the position of the about-content div
        setIndex(newIndex);
        gsap.set('.about-content', { x: 0 });
      }
    });
  };

  return (
    <div className="container">
      <section className="hero" id="hero">
        <div className="hero-content">
          <h1 className="first-line">Welcome to My Portfolio</h1>
        </div>
      </section>

      <section className="about" id="about">
        {/* Left Arrow */}
        <div className="arrow left" onClick={() => handleArrowClick('left')}>&#8592;</div>

        {/* About Me, Skills, Experience Elements */}
        <div className="about-content" style={{ transform: `translateX(-${index * 100}%)` }}>
          <div className="about-item">About Me</div>
          <div className="about-item">Skills</div>
          <div className="about-item">Experience</div>
        </div>

        {/* Right Arrow */}
        <div className="arrow right" onClick={() => handleArrowClick('right')}>&#8594;</div>

        {/* Rectangle Element */}
        <div className="rectangle"></div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
        </div>
      </section>
    </div>
  );
};

export default App;
