import React, { useState, Suspense, useRef } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css';
import ThreeDScene from "./components/AboutBG"; // 3D scene component

// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

const App = () => {
  const [activeIndex, setActiveIndex] = useState(0); // State for active carousel item
  const contentRef = useRef(null); // Ref for the content inside the content rectangle
  const headingRef = useRef(null); // Ref for the heading
  const carouselRef = useRef(null); // Ref for the entire carousel content (heading + content)

  const carouselItems = [
    {
      id: 'aboutMe',
      title: 'About Me',
      content: 'Content for About Me section...',
    },
    {
      id: 'skills',
      title: 'Skills',
      content: 'Content for Skills section...',
    },
    {
      id: 'experience',
      title: 'Experience',
      content: 'Content for Experience section...',
    },
  ];

  const navigateCarousel = (direction) => {
    const currentIndex = activeIndex;
    const newIndex =
      direction === 'left'
        ? (currentIndex === 0 ? carouselItems.length - 1 : currentIndex - 1)
        : (currentIndex === carouselItems.length - 1 ? 0 : currentIndex + 1);

    // Direction value for the animations
    const directionValue = direction === 'right' ? 100 : -100; // Right: 100, Left: -100

    // GSAP timeline to animate both heading and content simultaneously
    gsap.timeline()
      .to([headingRef.current, contentRef.current], {
        opacity: 0,
        xPercent: direction === 'right' ? 100 : -100, // Move content and heading in the clicked direction
        duration: 0.6,
        ease: 'power2.inOut', // Smooth easing for better animation
      })
      .call(() => {
        setActiveIndex(newIndex); // Update active index after content and heading slides out
        gsap.set([headingRef.current, contentRef.current], { opacity: 1, xPercent: direction === 'right' ? -100 : 100 }); // Set new content and heading to start from opposite direction
      })
      .to([headingRef.current, contentRef.current], {
        opacity: 1,
        xPercent: 0, // Slide content and heading in from the opposite direction
        duration: 0.6,
        ease: 'power2.inOut', // Smooth easing for both heading and content
      });
  };

  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50} />
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      <section className="about" id="about">
        <div className="carousel" ref={carouselRef}>
          {/* Section Heading */}
          <h2 className="section-heading" ref={headingRef}>
            {carouselItems[activeIndex].title}
          </h2>

          {/* Content Rectangle */}
          <div className="content-rectangle">
            {/* Content to be animated */}
            <div className="rectangle-content" ref={contentRef}>
              <p>{carouselItems[activeIndex].content}</p>
            </div>
          </div>

          {/* Arrow Buttons for Navigation */}
          <button
            className="arrow left-arrow"
            onClick={() => navigateCarousel('left')}
          >
            &#8592;
          </button>
          <button
            className="arrow right-arrow"
            onClick={() => navigateCarousel('right')}
          >
            &#8594;
          </button>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

export default App;
