import React, { useEffect, useState, Suspense, lazy } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import { useSpring, animated } from '@react-spring/web';

// Lazy load TitleBG component
const TitleBG = lazy(() => import('./components/TitleBG'));

// Animated Split Text Component using react-spring
const AnimatedText = ({ text, onAnimationComplete }) => {
  const splitText = text.split("");

  const springs = splitText.map((letter, index) =>
    useSpring({
      opacity: 1,
      transform: `translateY(0)`,
      from: { opacity: 0, transform: `translateY(20px)` },
      delay: index * 50,  // Adjusted delay for smoother appearance
      config: { duration: 600 },
      onRest: onAnimationComplete, // Trigger when animation is complete
    })
  );

  return (
    <animated.h1>
      {splitText.map((letter, index) => (
        <animated.span key={index} style={springs[index]}>
          {letter}
        </animated.span>
      ))}
    </animated.h1>
  );
};

const App = () => {
  const [isTitleBGVisible, setTitleBGVisible] = useState(false); // State to control TitleBG visibility
  const [isSectionLoaded, setSectionLoaded] = useState(false); // Track if the section is fully loaded

  const handleAnimationComplete = () => {
    setTitleBGVisible(true); // Show TitleBG after animation is complete
  };

  useEffect(() => {
    const sections = document.querySelectorAll('section');
    sections.forEach((section, sectionIndex) => {
      const lightConfig = getLightConfigForSection(sectionIndex) || [];
      lightConfig.forEach(({ color, count, positions }) => {
        for (let i = 0; i < count; i++) {
          const light = document.createElement('div');
          light.classList.add('light-effect');
          light.style.background = `radial-gradient(circle, ${color} 25%, ${color} 90%)`;
          light.style.boxShadow = `0 0 250px 150px ${color}`;

          const { positionX, positionY } = positions[i];
          light.style.left = `${positionX}%`;
          light.style.top = `${positionY}%`;
          section.appendChild(light);

          animateLightEffect(light);
        }
      });
    });

    // Optimized resize handling
    const debounceResize = debounce(() => {
      const aspect = window.innerWidth / window.innerHeight;
      const camera = document.querySelector('camera');
      camera.left = -2 * aspect;
      camera.right = 2 * aspect;
      camera.top = 2;
      camera.bottom = -2;
      camera.updateProjectionMatrix();
      window.dispatchEvent(new Event('resize'));
    }, 100);
    window.addEventListener('resize', debounceResize);

    return () => {
      window.removeEventListener('resize', debounceResize);
    };
  }, []);

  const getLightConfigForSection = (sectionIndex) => {
    switch (sectionIndex) {
      case 0: return [{ color: 'rgba(0, 255, 255, 0.3)', count: 1, positions: [{ positionX: 110, positionY: 0 }] }];
      case 1: return [{ color: 'rgba(255, 0, 255, 0.3)', count: 1, positions: [{ positionX: -15, positionY: 50 }] }];
      case 2: return [{ color: 'rgba(0, 255, 0, 0.3)', count: 1, positions: [{ positionX: 100, positionY: 50 }] }];
      case 3: return [{ color: 'rgba(255, 255, 0, 0.3)', count: 1, positions: [{ positionX: 20, positionY: 30 }] }];
      default: return [];
    }
  };

  const animateLightEffect = (lightElement) => {
    gsap.to(lightElement, {
      scale: 'random(1, 1.5)',
      opacity: 'random(0.4, 1)',
      x: 'random(-80, 80)',
      y: 'random(-80, 80)',
      duration: 4,
      repeat: -1,
      yoyo: true,
      ease: 'power1.inOut',
    });
  };

  // Lazy load section content one by one
  const loadSection = (index) => {
    setTimeout(() => {
      setSectionLoaded(true);
    }, 1000); // Delay for section to load smoothly
  };

  return (
    <div className="container">
      <section className="hero" id="hero">

        <div className="hero-content">
          <AnimatedText text="Welcome to My Portfolio" onAnimationComplete={handleAnimationComplete} />
          <p>3D Artist | Animator | Technical Support Engineer</p>
          <a href="#about" className="btn explore">Explore My Work</a>
        </div>
      </section>

      <section className="about" id="about">
        <div className="glass-effect">
          <h2>About Me</h2>
          <p>Some description about your work, skills, and experience.</p>
          <a href="#contact" className="btn contact">Contact Me</a>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

// Utility function to debounce resize events
const debounce = (func, delay) => {
  let timer;
  return function (...args) {
    if (timer) clearTimeout(timer);
    timer = setTimeout(() => func(...args), delay);
  };
};

export default App;
