import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';
import { gsap } from 'gsap';
import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass';
import { FilmPass } from 'three/examples/jsm/postprocessing/FilmPass';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const bigSphereRef = useRef(null);
  const smallSphereRef = useRef(null);
  const composerRef = useRef(null);  // Define composer reference

  useEffect(() => {
    const scene = new THREE.Scene();
    const aspect = window.innerWidth / window.innerHeight;
    const camera = new THREE.OrthographicCamera(
      -2 * aspect, // left
      2 * aspect,  // right
      2,           // top
      -2,          // bottom
      0.1,          // near
      1000          // far
    );
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.outputEncoding = THREE.sRGBEncoding;
    renderer.physicallyCorrectLights = true;
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    cameraRef.current = camera;
    sceneRef.current = scene;

    camera.position.z = 5;

    // Load HDR for lighting
    const rgbeLoader = new RGBELoader();
    rgbeLoader.load('/assets/brown_photostudio_02_2k.hdr', (texture) => {
      texture.mapping = THREE.EquirectangularReflectionMapping;
      scene.environment = texture;
      scene.background = new THREE.Color(0x000000);
    });

    // Load Image Texture for Big Sphere
    const textureLoader = new THREE.TextureLoader();
    const bigSphereTexture = textureLoader.load('/assets/your-image-texture.jpg'); // Replace with your image path

    // Big Sphere (Stationary) with Matte Material and Subtle Shine
    const bigSphereGeometry = new THREE.SphereGeometry(1, 32, 32);
    const bigSphereMaterial = new THREE.MeshStandardMaterial({
      map: bigSphereTexture, // Apply the loaded texture
      metalness: 0.1,  // Slightly metallic to give subtle reflections
      roughness: 0.9,  // Matte finish (high roughness)
    });
    const bigSphere = new THREE.Mesh(bigSphereGeometry, bigSphereMaterial);
    scene.add(bigSphere);
    bigSphereRef.current = bigSphere;

    // Load Image Texture for Small Sphere
    const smallSphereTexture = textureLoader.load('/assets/your-small-sphere-texture.jpg'); // Replace with your image path

    // Small Sphere (Will rotate around Z-axis) with Matte Material and Subtle Shine
    const smallSphereGeometry = new THREE.SphereGeometry(0.3, 32, 32);
    const smallSphereMaterial = new THREE.MeshStandardMaterial({
      map: smallSphereTexture, // Apply the loaded texture
      metalness: 0.1,  // Slightly metallic to give subtle reflections
      roughness: 0.9,  // Matte finish (high roughness)
    });
    const smallSphere = new THREE.Mesh(smallSphereGeometry, smallSphereMaterial);
    smallSphere.position.set(2, 0, 0); // Initial position behind the big sphere
    scene.add(smallSphere);
    smallSphereRef.current = smallSphere;

    const handleMouseMove = (event) => {
      const mouseXPosition = (event.clientX / window.innerWidth) * 2 - 1; // Normalized X-axis (-1 to 1)
      
      // GSAP animation for smooth movement
      const angle = mouseXPosition * Math.PI; // Map mouseX to an angle (-π to π)
      
      // Reduced orbit distance (radius), change the value from 2 to a smaller value like 1
      const radius = 1; // New smaller radius for the small sphere's orbit
      
      // Calculate the new position based on the reduced radius
      const targetX = radius * Math.cos(angle); // Orbit distance 1 (adjust to your preference)
      const targetY = radius * Math.sin(angle); // Orbit distance 1 (adjust to your preference)
      
      // Move the small sphere backwards in the Z-axis (negative Z value)
      const targetZ = -1; // Adjust this to move it further back (in negative direction)
      
      // Using GSAP to animate the small sphere's movement in a smooth manner
      gsap.to(smallSphere.position, {
        x: targetX,
        y: targetY,
        z: targetZ, // Move in Z-axis to make it go backwards
        duration: 0.5, // Smooth transition duration
        ease: 'power3.out', // Easing for smoothness
      });
    };
    
    window.addEventListener('mousemove', handleMouseMove);

    // Post-processing setup
    const composer = new EffectComposer(renderer);
    const renderPass = new RenderPass(scene, camera);
    composer.addPass(renderPass);

    // Film effect (for added texture and realism)
    const filmPass = new FilmPass(0.35, 0.025, 648, false);
    composer.addPass(filmPass);

    composerRef.current = composer;

    // Animation Loop
    const animate = () => {
      if (bigSphereRef.current) {
        // The big sphere stays stationary, but you can rotate it if needed
        bigSphereRef.current.rotation.y += 0.01; // Rotate big sphere (optional)
      }
      composer.render();
      requestAnimationFrame(animate);
    };
    animate();

    // Resize Event Listener
    const onWindowResize = () => {
      const aspect = window.innerWidth / window.innerHeight;
      camera.left = -2 * aspect;
      camera.right = 2 * aspect;
      camera.top = 2;
      camera.bottom = -2;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize);

    return () => {
      window.removeEventListener('resize', onWindowResize);
      window.removeEventListener('mousemove', handleMouseMove);
      renderer.dispose();
      bigSphereGeometry.dispose();
      bigSphereMaterial.dispose();
      smallSphereGeometry.dispose();
      smallSphereMaterial.dispose();
      scene.clear();
    };
  }, []);

  return null;
};

export default TitleBG;
