import React, { useEffect, useState, Suspense } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import { ScrollToPlugin } from 'gsap/ScrollToPlugin'; // Import the ScrollToPlugin
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css';
import ThreeDScene from './components/AboutBG'; // 3D scene component
// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

// Register ScrollToPlugin
gsap.registerPlugin(ScrollToPlugin);

const App = () => {
  const [activeIndex, setActiveIndex] = useState(0); // Index for active scroll item
  const sections = ['About Me', 'Section', 'Experience'];

  useEffect(() => {
    const leftArrow = document.querySelector('.left-arrow');
    const rightArrow = document.querySelector('.right-arrow');
    const sectionContainer = document.querySelector('.section-container');
    const sectionItems = document.querySelectorAll('.section');

    // Function to handle section transitions (stack animation)
    const transitionToSection = (index, direction) => {
      const currentSection = document.querySelector('.section.active');
      const nextSection = sectionItems[index];

      // Slide the current section out
      gsap.to(currentSection, {
        x: direction === 'right' ? '100%' : '-100%',
        opacity: 0,
        duration: 0.5,
        ease: 'power2.out',
      });

      // Slide the next section in
      gsap.to(nextSection, {
        x: 0,
        opacity: 1,
        visibility: 'visible',
        duration: 0.5,
        ease: 'power2.out',
      });
    };

    // Handle left arrow click
    const handleLeftClick = () => {
      setActiveIndex((prevIndex) => {
        const newIndex = prevIndex > 0 ? prevIndex - 1 : sections.length - 1;
        transitionToSection(newIndex, 'left');
        return newIndex;
      });
    };

    // Handle right arrow click
    const handleRightClick = () => {
      setActiveIndex((prevIndex) => {
        const newIndex = prevIndex < sections.length - 1 ? prevIndex + 1 : 0;
        transitionToSection(newIndex, 'right');
        return newIndex;
      });
    };

    // Attach event listeners to arrows
    if (leftArrow && rightArrow) {
      leftArrow.addEventListener('click', handleLeftClick);
      rightArrow.addEventListener('click', handleRightClick);
    }

    // Set the initial position for the first section
    const firstSection = sectionItems[activeIndex];
    gsap.set(firstSection, { x: 0, opacity: 1, visibility: 'visible' });

    // Cleanup event listeners on component unmount
    return () => {
      if (leftArrow && rightArrow) {
        leftArrow.removeEventListener('click', handleLeftClick);
        rightArrow.removeEventListener('click', handleRightClick);
      }
    };
  }, [activeIndex, sections.length]);

  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50} />
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      <section className="about" id="about">
        <div className="scroll-bar">
          <button className="scroll-arrow left-arrow">&#8592;</button>
          <div className="scroll-container">
            <div className="scroll-item">About Me</div>
            <div className="scroll-item">Section</div>
            <div className="scroll-item">Experience</div>
          </div>
          <button className="scroll-arrow right-arrow">&#8594;</button>
        </div>
        <div className="rectangle"></div>
        <div className="section-container">
          <div className="section active" id="about-me">
            About Me Content
          </div>
          <div className="section" id="section">
            Section Content
          </div>
          <div className="section" id="experience">
            Experience Content
          </div>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">
            Back to Top
          </a>
        </div>
      </section>
    </div>
  );
};

export default App;
