import React, { useState, useRef, useEffect } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import { useSpring, animated } from '@react-spring/web';

// Lazy load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));  // Dynamically import TitleBG
import Preloader from './components/Preloader';  // Import Preloader

// Animated Split Text Component using react-spring
const AnimatedText = React.memo(({ text, onAnimationComplete }) => {
  const splitText = text.split("");

  const springs = splitText.map((letter, index) =>
    useSpring({
      opacity: 1,
      transform: `translateY(0)`,
      from: { opacity: 0, transform: `translateY(20px)` },
      delay: index * 100,  // Delay for each letter
      config: { duration: 500 },
      onRest: index === splitText.length - 1 ? onAnimationComplete : undefined,  // Trigger callback when the last letter animates
    })
  );

  return (
    <animated.h1>
      {splitText.map((letter, index) => (
        <animated.span key={index} style={springs[index]}>
          {letter}
        </animated.span>
      ))}
    </animated.h1>
  );
});

const App = () => {
  const [animationCompleted, setAnimationCompleted] = useState(false);  // State to track animation completion
  const [preloaderVisible, setPreloaderVisible] = useState(true);  // State to control preloader visibility
  const [bgLoaded, setBgLoaded] = useState(false);  // Track when the TitleBG scene is fully loaded
  const [sceneLoaded, setSceneLoaded] = useState(false);  // Track if the scene is loaded already
  const titleBgRef = useRef(null);  // Reference for TitleBG component

  // Ensure TitleBG loads only once by controlling sceneLoaded state
  useEffect(() => {
    if (sceneLoaded) return; // Prevent reloading the scene if it's already loaded

    setSceneLoaded(true); // Mark the scene as loaded once TitleBG is rendered
  }, [sceneLoaded]);

  useEffect(() => {
    if (animationCompleted && titleBgRef.current) {
      // Animate the TitleBG after text animation completes (fade-in or scale-up)
      gsap.fromTo(titleBgRef.current, 
        { opacity: 0, scale: 0.8 },  // Initial state
        { opacity: 1, scale: 1, duration: 1, ease: 'power2.out' }  // Final state with smooth transition
      );
    }
  }, [animationCompleted]);

  useEffect(() => {
    // Hide preloader after TitleBG is fully loaded
    if (bgLoaded) {
      setPreloaderVisible(false);
    }
  }, [bgLoaded]);

  const handleAnimationComplete = () => {
    setAnimationCompleted(true);  // Mark animation as completed
  };

  const handleBgLoaded = () => {
    if (!bgLoaded) {  // Check to make sure TitleBG only triggers once
      setBgLoaded(true);  // Mark the background scene as loaded
    }
  };

  return (
    <div className="container">
      {/* Show Preloader until the TitleBG scene is ready */}
      {preloaderVisible && <Preloader />}

      <section className="hero" id="hero">
        {/* Load TitleBG only after it's fully loaded */}
        <React.Suspense fallback={<div>Loading background...</div>}>
          <div ref={titleBgRef}>
            <TitleBG onLoaded={handleBgLoaded} />  {/* Pass handleBgLoaded to TitleBG to track loading */}
          </div>
        </React.Suspense>

        <div className="hero-content">
          <AnimatedText 
            text="Welcome to My Portfolio" 
            onAnimationComplete={handleAnimationComplete}  // Pass callback to AnimatedText
          />
          <p>3D Artist | Animator | Technical Support Engineer</p>
          <a href="#about" className="btn explore">Explore My Work</a>
        </div>
      </section>

      <section className="about" id="about">
        <div className="glass-effect">
          <h2>About Me</h2>
          <p>Some description about your work, skills, and experience.</p>
          <a href="#contact" className="btn contact">Contact Me</a>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

export default App;
