import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cubeRef = useRef(null); // Add a ref to hold the cube

  useEffect(() => {
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer();
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    sceneRef.current = scene;

    // Create some 3D content (like a cube or sphere)
    const geometry = new THREE.BoxGeometry();
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material); // Use const here since it's not reassigned
    cubeRef.current = cube; // Store the cube in a ref
    scene.add(cube);
    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      if (cubeRef.current) { // Check if the cube exists
        cubeRef.current.rotation.x += 0.01;
        cubeRef.current.rotation.y += 0.01;
      }
      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    };
    animate();

    return () => {
      // Cleanup
      renderer.dispose();
      geometry.dispose();
      material.dispose();
      scene.remove(cubeRef.current);
      cubeRef.current = null; // Safely remove the reference
    };
  }, []);

  return <></>;
};

export default TitleBG;
