import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const bigSphereRef = useRef(null);
  const smallSphereRef = useRef(null);

  useEffect(() => {
    // Set up scene, camera, and renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.outputEncoding = THREE.sRGBEncoding; // Optimize color output
    renderer.physicallyCorrectLights = true; // Enable realistic lighting calculations
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    cameraRef.current = camera;
    sceneRef.current = scene;

    // Set camera position
    camera.position.z = 5;

    // Load HDR for lighting only
    const rgbeLoader = new RGBELoader();
    rgbeLoader.load('/assets/brown_photostudio_02_2k.hdr', (texture) => {
      texture.mapping = THREE.EquirectangularReflectionMapping;
      scene.environment = texture; // Use HDR for lighting
      scene.background = new THREE.Color(0x000000); // Black background
    });

    // Create the big sphere
    const bigSphereMaterial = new THREE.MeshPhysicalMaterial({
        color: 0xffffff, // White color
        metalness: 0.2,  // Low metalness for a matte-like look
        roughness: 0.8,  // High roughness for a matte surface
        clearcoat: 0.1,  // Subtle clearcoat for a light reflection layer
        clearcoatRoughness: 0.5, // Smooth clearcoat reflection
      });
    const bigSphere = new THREE.Mesh(bigSphereGeometry, bigSphereMaterial);
    scene.add(bigSphere);
    bigSphereRef.current = bigSphere;

    // Create the small sphere
    const smallSphereMaterial = new THREE.MeshPhysicalMaterial({
        color: 0xff0000, // Red color
        metalness: 0.3,  // Slightly more metallic for contrast
        roughness: 0.7,  // Moderate roughness for a softer matte look
        clearcoat: 0.15, // Subtle clearcoat for light interaction
        clearcoatRoughness: 0.5, // Smooth clearcoat reflection
      });
    const smallSphere = new THREE.Mesh(smallSphereGeometry, smallSphereMaterial);
    smallSphere.position.set(1.5, 0, 0); // Place it next to the big sphere
    scene.add(smallSphere);
    smallSphereRef.current = smallSphere;

    // Animation loop
    const animate = () => {
      if (bigSphereRef.current && smallSphereRef.current) {
        bigSphereRef.current.rotation.y += 0.01; // Rotate big sphere
        smallSphereRef.current.rotation.y -= 0.02; // Rotate small sphere
        smallSphereRef.current.position.x = 1.5 * Math.cos(Date.now() * 0.001); // Orbit small sphere
        smallSphereRef.current.position.z = 1.5 * Math.sin(Date.now() * 0.001);
      }
      renderer.render(scene, camera);
      requestAnimationFrame(animate);
    };
    animate();

    // Resize listener for responsive rendering
    const onWindowResize = () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize);

    // Clean up on unmount
    return () => {
      window.removeEventListener('resize', onWindowResize);
      renderer.dispose();
      bigSphereGeometry.dispose();
      bigSphereMaterial.dispose();
      smallSphereGeometry.dispose();
      smallSphereMaterial.dispose();
      scene.clear();
    };
  }, []);

  return null;
};

export default TitleBG;
