import React, { useEffect, useState, Suspense } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import ShinyText from './blocks/TextAnimations/ShinyText/ShinyText';
import SplitText from './blocks/TextAnimations/SplitText/SplitText';
import './blocks/TextAnimations/ShinyText/ShinyText.css'
import ThreeDScene from "./components/AboutBG"; // 3D scene component
// Lazy-load TitleBG component
const TitleBG = React.lazy(() => import('./components/TitleBG'));

const App = () => {
  const [isTitleBGVisible, setTitleBGVisible] = useState(true); // Set to true to load TitleBG immediately


// Get the scroll container, sections, and arrows
const scrollContainer = document.querySelector('.scroll-container');
const sections = document.querySelectorAll('.section');
const leftArrow = document.querySelector('.left-arrow');
const rightArrow = document.querySelector('.right-arrow');

// Set the initial active section
let activeIndex = 0;

// Function to update active section
function setActiveSection(index) {
  sections.forEach((section, i) => {
    section.classList.remove('active'); // Remove active class from all sections
    if (i === index) {
      section.classList.add('active'); // Add active class to the current section
    }
  });
}

// Function to scroll left
leftArrow.addEventListener('click', () => {
  activeIndex = (activeIndex > 0) ? activeIndex - 1 : sections.length - 1;
  setActiveSection(activeIndex);
});

// Function to scroll right
rightArrow.addEventListener('click', () => {
  activeIndex = (activeIndex < sections.length - 1) ? activeIndex + 1 : 0;
  setActiveSection(activeIndex);
});

// Initialize the first section as active
setActiveSection(activeIndex);


  return (
    <div className="container">
      <section className="hero" id="hero">
        <Suspense fallback={<div>Loading background...</div>}>
          <TitleBG />
        </Suspense>
        <div className="hero-content">
          {/* Apply SplitText to the Hero text */}
          <h1 className="first-line">
            <SplitText text="WELCOME TO MY" animation="fadeIn" delay={50}/>
          </h1>
          <h2 className="second-line">
            <SplitText text="PORTFOLIO" animation="fadeIn" delay={50} />
          </h2>
          <p className="subheading">
            <SplitText text="3D Artist | Animator" animation="fadeIn" delay={100} />
          </p>
          <button className="btn explore">
            <ShinyText text="Explore My Work" />
          </button>
        </div>
      </section>

      <section className="about" id="about">
  <div className="scroll-bar">
    <button className="scroll-arrow left-arrow">&#8592;</button>
    <div className="scroll-container">
      <div className="scroll-item">About Me</div>
      <div className="scroll-item">Section</div>
      <div className="scroll-item">Experience</div>
    </div>
    <button className="scroll-arrow right-arrow">&#8594;</button>
  </div>
  <div className="rectangle"></div>
  <div className="section-container">
    <div className="section" id="about-me">About Me Content</div>
    <div className="section" id="section">Section Content</div>
    <div className="section" id="experience">Experience Content</div>
  </div>
</section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

// Throttle function for IntersectionObserver callback
const throttle = (func, wait) => {
  let timeout = null;
  return function () {
    const context = this;
    const args = arguments;
    if (!timeout) {
      timeout = setTimeout(() => {
        timeout = null;
        func.apply(context, args);
      }, wait);
    }
  };
};

export default App;
