import React, { useEffect, useRef } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import * as THREE from 'three';  // Import Three.js

const App = () => {
  const canvasRef = useRef(null);  // Reference for the canvas

  useEffect(() => {
    // Set up the scene, camera, and renderer
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ canvas: canvasRef.current });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.body.appendChild(renderer.domElement);

    // Create a rotating cube as a background element
    const geometry = new THREE.BoxGeometry(1, 1, 1);
    const material = new THREE.MeshBasicMaterial({ color: 0x00ff00 });
    const cube = new THREE.Mesh(geometry, material);
    scene.add(cube);

    // Position the camera
    camera.position.z = 5;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);

      // Rotate the cube
      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;

      renderer.render(scene, camera);
    };

    animate();

    // Handle window resize
    const onResize = () => {
      const width = window.innerWidth;
      const height = window.innerHeight;
      renderer.setSize(width, height);
      camera.aspect = width / height;
      camera.updateProjectionMatrix();
    };

    window.addEventListener('resize', onResize);

    return () => {
      window.removeEventListener('resize', onResize);
    };
  }, []);

  useEffect(() => {
    const sections = document.querySelectorAll('section');
    sections.forEach((section, sectionIndex) => {
      // Get the light configuration for each section
      const lightConfig = getLightConfigForSection(sectionIndex);

      lightConfig.forEach(({ color, count, positions }) => {
        // Create the specified number of lights
        for (let i = 0; i < count; i++) {
          const light = document.createElement('div');
          light.classList.add('light-effect');

          // Set core and emission colors for each light
          light.style.background = `radial-gradient(circle, ${color} 25%, ${color} 90%)`;
          light.style.boxShadow = `0 0 250px 150px ${color}`;

          // Control placement of each light based on 'positions'
          const { positionX, positionY } = positions[i];

          light.style.left = `${positionX}%`;
          light.style.top = `${positionY}%`;

          section.appendChild(light);

          // Animate each light with GSAP
          animateLightEffect(light);
        }
      });
    });
  }, []);

  // Function to get light configuration based on the section index
  const getLightConfigForSection = (sectionIndex) => {
    switch (sectionIndex) {
      case 0: // Hero Section
        return [
          {
            color: 'rgba(0, 255, 255, 0.3)', // Cyan
            count: 1,
            positions: [
              { positionX: 110, positionY: 0 },
            ],
          },
        ];
      case 1: // About Section
        return [
          {
            color: 'rgba(255, 0, 255, 0.3)', // Magenta
            count: 1,
            positions: [
              { positionX: -15, positionY: 50 },
            ],
          },
        ];
      case 2: // Portfolio Section
        return [
          {
            color: 'rgba(0, 255, 0, 0.3)', // Green
            count: 1,
            positions: [
              { positionX: 100, positionY: 50 },
            ],
          },
        ];
      case 3: // Contact Section
        return [
          {
            color: 'rgba(255, 255, 0, 0.3)', // Yellow
            count: 1,
            positions: [
              { positionX: 20, positionY: 30 },
            ],
          },
        ];
      default:
        return [];
    }
  };

  // Animate light effect using GSAP
  const animateLightEffect = (lightElement) => {
    gsap.to(lightElement, {
      scale: 'random(1, 1.5)',
      opacity: 'random(0.4, 1)',
      x: 'random(-80, 80)',
      y: 'random(-80, 80)',
      duration: 4,
      repeat: -1,
      yoyo: true,
      ease: 'power1.inOut',
    });
  };

  // Intersection Observer for Section Animations
  useEffect(() => {
    const observer = new IntersectionObserver((entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          gsap.to(entry.target, {
            opacity: 1,
            y: 0,
            duration: 1.2,
            ease: 'power3.out',
          });
        } else {
          gsap.set(entry.target, {
            opacity: 0.5,
            y: 100,
          });
        }
      });
    }, {
      threshold: 0.2,
    });

    const sections = document.querySelectorAll('section');
    sections.forEach((section) => {
      observer.observe(section);
      gsap.set(section, { opacity: 0.5, y: 100 });
    });

    return () => observer.disconnect();
  }, []);

  return (
    <div className="container">
      <section className="hero" id="hero">
        <canvas ref={canvasRef} className="hero-canvas"></canvas> {/* Add canvas for 3D scene */}
        <div className="hero-content">
          <h1>Welcome to My Portfolio</h1>
          <p>3D Artist | Animator | Technical Support Engineer</p>
          <a href="#about" className="btn explore">Explore My Work</a>
        </div>
      </section>

      <section className="about" id="about">
        <div className="glass-effect">
          <h2>About Me</h2>
          <p>Some description about your work, skills, and experience.</p>
          <a href="#contact" className="btn contact">Contact Me</a>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

export default App;
