import React, { useRef, useEffect } from 'react';
import * as THREE from 'three';
import { RGBELoader } from 'three/examples/jsm/loaders/RGBELoader';
import { gsap } from 'gsap';
import { throttle } from 'lodash';  // Throttling library
import { EffectComposer } from 'three/examples/jsm/postprocessing/EffectComposer';
import { RenderPass } from 'three/examples/jsm/postprocessing/RenderPass';
import { FilmPass } from 'three/examples/jsm/postprocessing/FilmPass';

const TitleBG = () => {
  const sceneRef = useRef(null);
  const rendererRef = useRef(null);
  const cameraRef = useRef(null);
  const bigSphereRef = useRef(null);
  const smallSphereRef = useRef(null);
  const composerRef = useRef(null);

  useEffect(() => {
    const scene = new THREE.Scene();
    const aspect = window.innerWidth / window.innerHeight;
    const camera = new THREE.OrthographicCamera(
      -2 * aspect, 2 * aspect, 2, -2, 0.1, 1000
    );
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    renderer.outputEncoding = THREE.sRGBEncoding;
    renderer.physicallyCorrectLights = true;
    document.getElementById('hero').appendChild(renderer.domElement);
    rendererRef.current = renderer;
    cameraRef.current = camera;
    sceneRef.current = scene;
    camera.position.z = 5;

    // Load HDR for lighting
    const rgbeLoader = new RGBELoader();
    rgbeLoader.load('/assets/brown_photostudio_02_2k.hdr', (texture) => {
      texture.mapping = THREE.EquirectangularReflectionMapping;
      scene.environment = texture;
      scene.background = new THREE.Color(0x000000);
    });

    // Textures and Materials (with lower segment counts for performance)
    const textureLoader = new THREE.TextureLoader();
    const bigSphereTexture = textureLoader.load('assets/Poliigon_MetalPaintedMatte_7037_BaseColor.jpg');
    const smallSphereTexture = textureLoader.load('assets/Poliigon_MetalPaintedMatte_7037_BaseColor.jpg');

    const bigSphereMaterial = new THREE.MeshStandardMaterial({
      map: bigSphereTexture, metalness: 0.1, roughness: 0.85, envMapIntensity: 0.4
    });
    const smallSphereMaterial = new THREE.MeshStandardMaterial({
      color: 0xFF5349, // Orange color
      emissive: 0xFF6A00, // Glow color (same as the material color)
      emissiveIntensity: 0.8, // Adjust the intensity of the glow
      metalness: 0.1, // Set metalness to 0.1 for a slightly matte finish
      roughness: 0.85, // Roughness for a non-shiny surface
      envMapIntensity: 0.4 // For environment map reflection
    });
    const bigSphereGeometry = new THREE.SphereGeometry(1, 32, 32); // Lower segments
    const smallSphereGeometry = new THREE.SphereGeometry(0.3, 32, 32); // Lower segments

    const bigSphere = new THREE.Mesh(bigSphereGeometry, bigSphereMaterial);
    const smallSphere = new THREE.Mesh(smallSphereGeometry, smallSphereMaterial);
    smallSphere.position.set(1, 0, 0);
    scene.add(bigSphere, smallSphere);
    bigSphereRef.current = bigSphere;
    smallSphereRef.current = smallSphere;

    // Lights (Reduce intensity or remove shadows if not needed)
    const directionalLight1 = new THREE.DirectionalLight(0xffffff, 0.1);
    directionalLight1.position.set(3, 3, 3);
    directionalLight1.target.position.set(0, 0, 0);
    scene.add(directionalLight1, directionalLight1.target);

    const directionalLight2 = new THREE.DirectionalLight(0xffffff, 0.5);
    directionalLight2.position.set(-3, -3, 3);
    directionalLight2.target.position.set(0, 0, 0);
    scene.add(directionalLight2, directionalLight2.target);

    // Handle Mouse Move with Throttle (Including Tilt Effect)
    const handleMouseMove = throttle((event) => {
      const mouseXPosition = (event.clientX / window.innerWidth) * 2 - 1;
      const mouseYPosition = -(event.clientY / window.innerHeight) * 2 + 1; // Inverted Y for correct direction

      const radius = 1; // You can adjust this for a bigger or smaller movement range

      // Calculate the target position for the small sphere (for the cursor interaction)
      const targetX = radius * Math.cos(mouseXPosition * Math.PI);
      const targetY = radius * Math.sin(mouseYPosition * Math.PI);
      const targetZ = -1;

      // Animate the small sphere position based on the cursor position
      gsap.to(smallSphere.position, {
        x: targetX,
        y: targetY,
        z: targetZ,
        duration: 0.5,
        ease: 'power3.out',
      });

      // Apply a tilt effect to the entire scene
      const tiltX = mouseYPosition * 0.2; // You can adjust the multiplier for tilt intensity
      const tiltY = mouseXPosition * 0.2; // Adjust tilt intensity along the Y axis

      // Apply the tilt effect to the camera (camera rotation will create the parallax effect)
      gsap.to(camera.rotation, {
        x: tiltX,
        y: tiltY,
        duration: 0.5, // Adjust duration for smoothness
        ease: 'power3.out',
      });

      // Alternatively, you can apply the tilt to the entire scene instead of just the camera
      // scene.rotation.x = tiltX;
      // scene.rotation.y = tiltY;
    }, 1); // Throttle to 20ms

    window.addEventListener('mousemove', handleMouseMove);

    // Post-processing setup
    const composer = new EffectComposer(renderer);
    composer.addPass(new RenderPass(scene, camera));
    composer.addPass(new FilmPass(0.2, 0.025, 648, false)); // Lower effect strength
    composerRef.current = composer;

    // Animation Loop
    const animate = () => {
      composer.render();
      requestAnimationFrame(animate);
    };
    animate();

    // Resize Event Listener
    const onWindowResize = () => {
      const aspect = window.innerWidth / window.innerHeight;
      camera.left = -2 * aspect;
      camera.right = 2 * aspect;
      camera.top = 2;
      camera.bottom = -2;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    };
    window.addEventListener('resize', onWindowResize);

    return () => {
      window.removeEventListener('resize', onWindowResize);
      window.removeEventListener('mousemove', handleMouseMove);
      renderer.dispose();
      bigSphereGeometry.dispose();
      bigSphereMaterial.dispose();
      smallSphereGeometry.dispose();
      smallSphereMaterial.dispose();
      scene.clear();
    };
  }, []);

  return null;
};

export default TitleBG;
