import React, { useEffect } from 'react';
import './styles/App.css';
import * as THREE from 'three';
import gsap from 'gsap';

const App = () => {
  useEffect(() => {
    // Set up 3D scene
    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    document.getElementById('hero').appendChild(renderer.domElement);

    // Create a 3D object (e.g., a rotating sphere)
    const geometry = new THREE.SphereGeometry(5, 32, 32);
    const material = new THREE.MeshBasicMaterial({ color: 0x44aa88, wireframe: true });
    const sphere = new THREE.Mesh(geometry, material);
    scene.add(sphere);

    // Position the camera
    camera.position.z = 15;

    // Animation loop
    const animate = () => {
      requestAnimationFrame(animate);

      // Rotate the sphere
      sphere.rotation.x += 0.01;
      sphere.rotation.y += 0.01;

      // Render the scene
      renderer.render(scene, camera);
    };

    animate();

    // Resize the renderer when the window is resized
    window.addEventListener('resize', () => {
      camera.aspect = window.innerWidth / window.innerHeight;
      camera.updateProjectionMatrix();
      renderer.setSize(window.innerWidth, window.innerHeight);
    });

    return () => {
      // Cleanup when the component is unmounted
      renderer.dispose();
      window.removeEventListener('resize', () => {});
    };
  }, []);

    return (
      <div className="container">
        <section className="hero" id="hero">
          <TitleBG /> {/* Render the 3D background here */}
          <div className="hero-content">
            <h1>Welcome to My Portfolio</h1>
            <p>3D Artist | Animator | Technical Support Engineer</p>
            <a href="#about" className="btn explore">Explore My Work</a>
          </div>
        </section>
  
        <section className="about" id="about">
          <div className="glass-effect">
            <h2>About Me</h2>
            <p>Some description about your work, skills, and experience.</p>
            <a href="#contact" className="btn contact">Contact Me</a>
          </div>
        </section>
  
        <section className="portfolio" id="portfolio">
          <div className="glass-effect">
            <h2>My Portfolio</h2>
            <p>Showcase of projects like 3D models, animations, etc.</p>
          </div>
        </section>
  
        <section className="contact" id="contact">
          <div className="glass-effect">
            <h2>Contact</h2>
            <p>Get in touch with me.</p>
            <a href="#hero" className="btn contact">Back to Top</a>
          </div>
        </section>
      </div>
    );
  };
  
  export default App;
