import React, { useEffect, useState, useMemo, useCallback } from 'react';
import './styles/App.css';
import gsap from 'gsap';
import { useSpring, animated } from '@react-spring/web';
import TitleBG from './components/TitleBG'; // Import TitleBG component

// Animated Split Text Component using react-spring
const AnimatedText = ({ text, onAnimationComplete }) => {
  // Split the text into individual letters
  const splitText = text.split("");

  // Use react-spring for each letter's animation
  const springs = splitText.map((letter, index) =>
    useSpring({
      opacity: 1,
      transform: `translateY(0)`,
      from: { opacity: 0, transform: `translateY(20px)` },
      delay: index * 100,  // Delay for each letter
      config: { duration: 500 },
      onRest: onAnimationComplete,  // Trigger when animation is complete
    })
  );

  return (
    <animated.h1>
      {splitText.map((letter, index) => (
        <animated.span key={index} style={springs[index]}>
          {letter}
        </animated.span>
      ))}
    </animated.h1>
  );
};

const App = () => {
  const [isTitleBGVisible, setTitleBGVisible] = useState(false); // State to control TitleBG visibility

  // Callback to be called when animation finishes
  const handleAnimationComplete = useCallback(() => {
    setTitleBGVisible(true); // Show TitleBG after animation is complete
  }, []);

  // Memoize light configurations to avoid recalculating on every render
  const lightConfig = useMemo(() => {
    const configs = [
      {
        color: 'rgba(0, 255, 255, 0.3)', // Cyan for Hero Section
        count: 1,
        positions: [{ positionX: 110, positionY: 0 }],
      },
      {
        color: 'rgba(255, 0, 255, 0.3)', // Magenta for About Section
        count: 1,
        positions: [{ positionX: -15, positionY: 50 }],
      },
      {
        color: 'rgba(0, 255, 0, 0.3)', // Green for Portfolio Section
        count: 1,
        positions: [{ positionX: 100, positionY: 50 }],
      },
      {
        color: 'rgba(255, 255, 0, 0.3)', // Yellow for Contact Section
        count: 1,
        positions: [{ positionX: 20, positionY: 30 }],
      },
    ];
    return configs;
  }, []);

  // Animate light effect using GSAP
  const animateLightEffect = useCallback((lightElement) => {
    gsap.to(lightElement, {
      scale: 'random(1, 1.5)',
      opacity: 'random(0.4, 1)',
      x: 'random(-80, 80)',
      y: 'random(-80, 80)',
      duration: 4,
      repeat: -1,
      yoyo: true,
      ease: 'power1.inOut',
    });
  }, []);

  // Set up event listeners once for throttling
  useEffect(() => {
    const sections = document.querySelectorAll('section');
    sections.forEach((section, sectionIndex) => {
      const lightConfigForSection = lightConfig[sectionIndex];
      lightConfigForSection.forEach(({ color, count, positions }) => {
        for (let i = 0; i < count; i++) {
          const light = document.createElement('div');
          light.classList.add('light-effect');
          light.style.background = `radial-gradient(circle, ${color} 25%, ${color} 90%)`;
          light.style.boxShadow = `0 0 250px 150px ${color}`;

          const { positionX, positionY } = positions[i];
          light.style.left = `${positionX}%`;
          light.style.top = `${positionY}%`;

          section.appendChild(light);
          animateLightEffect(light);
        }
      });
    });

    // Throttle resize and mousemove events
    const throttledMouseMove = throttle(handleMouseMove, 20);
    window.addEventListener('mousemove', throttledMouseMove);

    const handleResize = throttle(() => {
      // Handle resize logic
      console.log('Window resized');
    }, 200);
    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('mousemove', throttledMouseMove);
      window.removeEventListener('resize', handleResize);
    };
  }, [lightConfig, animateLightEffect]);

  // Throttled mouse move handler
  const handleMouseMove = (event) => {
    const mouseXPosition = (event.clientX / window.innerWidth) * 2 - 1;
    const mouseYPosition = (event.clientY / window.innerHeight) * 2 - 1;
    const angleX = mouseXPosition * Math.PI;
    const angleY = mouseYPosition * Math.PI;
    const radius = 1;
    const targetX = radius * Math.cos(angleX);
    const targetY = radius * Math.sin(angleY);
    gsap.to('.small-sphere', {
      x: targetX,
      y: targetY,
      duration: 0.5,
      ease: 'power3.out',
    });
  };

  return (
    <div className="container">
      <section className="hero" id="hero">
        {isTitleBGVisible && <TitleBG />}
        <div className="hero-content">
          <AnimatedText text="Welcome to My Portfolio" onAnimationComplete={handleAnimationComplete} />
          <p>3D Artist | Animator | Technical Support Engineer</p>
          <a href="#about" className="btn explore">Explore My Work</a>
        </div>
      </section>

      <section className="about" id="about">
        <div className="glass-effect">
          <h2>About Me</h2>
          <p>Some description about your work, skills, and experience.</p>
          <a href="#contact" className="btn contact">Contact Me</a>
        </div>
      </section>

      <section className="portfolio" id="portfolio">
        <div className="glass-effect">
          <h2>My Portfolio</h2>
          <p>Showcase of projects like 3D models, animations, etc.</p>
        </div>
      </section>

      <section className="contact" id="contact">
        <div className="glass-effect">
          <h2>Contact</h2>
          <p>Get in touch with me.</p>
          <a href="#hero" className="btn contact">Back to Top</a>
        </div>
      </section>
    </div>
  );
};

export default App;
