import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';
import '/'; // Add CSS for the SkillCard styling

const SkillCard = ({ skill, progress }) => {
  const [hovered, setHovered] = useState(false);

  const cardAnimation = useSpring({
    transform: hovered ? 'scale(1.05)' : 'scale(1)',
    boxShadow: hovered ? '0px 10px 20px rgba(0, 0, 0, 0.2)' : '0px 5px 15px rgba(0, 0, 0, 0.1)',
  });

  const progressBarAnimation = useSpring({
    width: hovered ? `${progress}%` : '0%',
  });

  return (
    <animated.div
      className="skill-card"
      style={cardAnimation}
      onMouseEnter={() => setHovered(true)}
      onMouseLeave={() => setHovered(false)}
    >
      <div className="skill-title">{skill}</div>
      <div className="progress-bar-container">
        <animated.div className="progress-bar" style={progressBarAnimation} />
      </div>
    </animated.div>
  );
};

export default SkillCard;
